import NavSections from './navSections/NavSections'

import styles from './Navbar.module.css';

export default class Navbar extends Component {

    state = {
        scrolled: false,
        showNavbar: false,
        myClass: 'toggleButton'
    }

    largeClickHandler = () => {
        this.setState({
            myClass: 'large'
