import React, {Component} from 'react';

import ProjectCard from './projectCard/ProjectCard'
import styles from './MoreProjects.module.css'

import data from '../../../data/Data'


const moreProjects = data.projects;

// const projects = [
//     <li className={styles.project}>
//         <ProjectCard
//             name={moreProjects[0].name}
//             title="Desktop Web Application to find study spots on campus"
//             description="Night Owls is web application that makes finding 
//                         study spots easier on campus. The application allows 
//                         students to see where the study spots are with real-time 
//                         spots availability."
//         />
//     </li>,   
// ]

class MoreProjects extends Component {

    state = {
        showMoreProjects: false
    }


    showLessProjects = () => {
        this.setState({
            showMoreProjects: false
        })
    }

    showMoreProjects = () => {
        console.log("More");
        this.setState({
            showMoreProjects: true
        })
    }

    render(){

        const projectss = data.map(project => {
            return (
                <li className={styles.project}>
                    <ProjectCard
                        name={moreProjects[0].name}
                        title="Desktop Web Application to find study spots on campus"
                        description="Night Owls is web application that makes finding 
                                    study spots easier on campus. The application allows 
                                    students to see where the study spots are with real-time 
                                    spots availability."
                    />
                </li> 
            )
        })

        let displayProjects = projects.slice(0,3);

        let projectsToggleButton = (
            <div 
                id={styles.seeMoreButton}
                onClick={this.showMoreProjects}
            >
                See More Projects<i className="fas fa-long-arrow-alt-right"></i>
            </div>
        )

        if(this.state.showMoreProjects) {
            displayProjects = projects;

            projectsToggleButton = (
                <div 
                    id={styles.seeMoreButton}
                    onClick={this.showLessProjects}
                >
                    See Less Projects<i className="fas fa-long-arrow-alt-right"></i>
                </div>
            )
        }

        return (
            <div className={styles.content}>

                <ul className={styles.cards}>
                    {displayProjects}
                </ul>

                {projectsToggleButton}
            </div>
        )
    }
}

export default MoreProjects;
